import axios from 'axios';
import React, {useEffect, useState} from 'react';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState(null);
  const [num1, setNum1] = useState();
  const [num2, setNum2] = useState();
  const [answer, setAnswer] = useState('');
  const [result, setResult] = useState('');
  const [leaderboard, setLeaderboard] = useState([]);


  const handleLogin = async () => {
    try {
      const response = await axios.post(
        'http://localhost:3001/login', {
        username: username,
        password: password
      },
      {headers: {"Content-Type": "application/json"}});
      console.log(response.data);
      setMessage('Login successful');
    } catch (error) {
      console.log(error);
      setMessage('Invalid username/password')
    }
  };

  const handleCreate = async () => {
    try {
      const response = await axios.post(
        'http://localhost:3001/signup', {
        username: username,
        password: password
      },
      {headers: {"Content-Type": "application/json"}});
      console.log(response.data);
      setMessage(`Welcome ${username}! Please use your credentials to login`);
    } catch (error) {
      console.log(error);
      setMessage('Sorry, that username is taken!');
    }
  };

  const generateQuestion = () =>{
    const num1 = Math.floor(Math.random() * 100) + 1;
    const num2 = Math.floor(Math.random() * 100) + 1;
    setNum1(num1);
    setNum2(num2);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const sum = num1 + num2;
    const userAnswer = parseInt(answer);
    const result = userAnswer === sum ? 'correct' : 'incorrect';

    try {
      const response = await axios.post('http://localhost:3001/update', {
        username: username,
        result: result,
      });
      console.log(response.data);
      setResult(result);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect (() => {
    const fetchLeaderboard = async () => {
      try{
        const response = await axios.get('http://localhost:3001/leaderboard');
        console.log(response.data);
        setLeaderboard(response.data.leaders);
      }
      catch (error){
        console.log(error);
      }
    };
    fetchLeaderboard();
  }, []);

  return (
    <div>
      <label>Username: </label>
      <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
      <br />
      <label>Password: </label>
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <br />
      <button onClick={handleLogin}>Login</button>
      <button onClick={handleCreate}>Create User</button>
      {message && (
        <p style={{ color: message.includes('Welcome') || message.includes('successful') ? 'green' : 'red' }}>
          {message}
        </p>
      )}
      <form onSubmit={handleSubmit}>
        <label>
          {num1} + {num2} ={' '}
          <input type="text" value={answer} onChange={(e) => setAnswer(e.target.value)} />
        </label>
        <button type="submit">Answer</button>
      </form>
      {result && (
        <div style={{ backgroundColor: result === 'correct' ? 'green' : 'red' }}>
          <h2>{result === 'correct' ? 'Correct!' : 'Incorrect!'}</h2>
          <ul>
            {leaderboard.map((leader) => (
              <li key={leader}>{leader}</li>
            ))}
          </ul>
          <button onClick={generateNumbers}>Next</button>
        </div>
      )}
    </div>

  );
}
export default App;
